mapper.select:

select

  distinct


from fa_invoice




  order by ${orderByClause}


  limit #{page.length} offset #{page.begin}

    select 

    from fa_invoice


        fa_no = #{faNo,jdbcType=VARCHAR}


        fa_time = #{faTime,jdbcType=TIMESTAMP}


        fa_phone = #{faPhone,jdbcType=VARCHAR}


        fa_point = #{faPoint,jdbcType=BIGINT}


        fa_clientele = #{faClientele,jdbcType=BIGINT}


        so_phone = #{soPhone,jdbcType=VARCHAR}


        so_clientele = #{soClientele,jdbcType=BIGINT}


        so_point = #{soPoint,jdbcType=BIGINT}


        freight = #{freight,jdbcType=INTEGER}


        collection = #{collection,jdbcType=INTEGER}


        ins_time = #{insTime,jdbcType=TIMESTAMP}


        up_time = #{upTime,jdbcType=TIMESTAMP}


        status = #{status,jdbcType=INTEGER}


        count_insurance = #{countInsurance,jdbcType=INTEGER}


        fee = #{fee,jdbcType=INTEGER}


        freight_way = #{freightWay,jdbcType=INTEGER}


        collection_way = #{collectionWay,jdbcType=INTEGER}


        bank_code = #{bankCode,jdbcType=VARCHAR}


        card_no = #{cardNo,jdbcType=VARCHAR}


        so_way = #{soWay,jdbcType=INTEGER}


        so_name like concat('%',#{soName,jdbcType=VARCHAR},'%')



	      limit #{page.length} offset #{page.begin}

 select 
	count(*)
 from fa_invoice


     and fa_no = #{faNo,jdbcType=VARCHAR}


    and  fa_time = #{faTime,jdbcType=TIMESTAMP}


     and fa_phone = #{faPhone,jdbcType=VARCHAR}


     and fa_point = #{faPoint,jdbcType=BIGINT}


     and fa_clientele = #{faClientele,jdbcType=BIGINT}


     and so_phone = #{soPhone,jdbcType=VARCHAR}


     and so_clientele = #{soClientele,jdbcType=BIGINT}


     and so_point = #{soPoint,jdbcType=BIGINT}


     and freight = #{freight,jdbcType=INTEGER}


     and collection = #{collection,jdbcType=INTEGER}


     and ins_time = #{insTime,jdbcType=TIMESTAMP}


     and up_time = #{upTime,jdbcType=TIMESTAMP}


     and status = #{status,jdbcType=INTEGER}


     and count_insurance = #{countInsurance,jdbcType=INTEGER}


     and fee = #{fee,jdbcType=INTEGER}


     and freight_way = #{freightWay,jdbcType=INTEGER}


     and collection_way = #{collectionWay,jdbcType=INTEGER}


    and  bank_code = #{bankCode,jdbcType=VARCHAR}


   and  card_no = #{cardNo,jdbcType=VARCHAR}


    and  so_way = #{soWay,jdbcType=INTEGER}


    and so_name like concat('%',#{soName,jdbcType=VARCHAR},'%')

  	SELECT
		fi.*,
		sps.`name` sps_name,
		spf.`name` spf_name,
		bcs.`name` bcs_name,
		bcs.phone bcs_phone,
		bcf.`name` bcf_name,
		bcf.phone bcf_phone
	FROM
		fa_invoice fi,
		sys_point sps,
		sys_point spf,
		bas_clientele bcs,
		bas_clientele bcf

		fi.fa_point = spf.id
		AND fi.so_point = sps.id
		AND fi.fa_clientele = bcf.id
		AND fi.so_clientele = bcs.id

        and fi.fa_no = #{faNo,jdbcType=VARCHAR}


       and  TO_DAYS(fi.fa_time) = TO_DAYS(#{faTime,jdbcType=TIMESTAMP})


        and fi.fa_phone = #{faPhone,jdbcType=VARCHAR}


      and (fi.fa_point = #{faPoint,jdbcType=BIGINT} and fi.so_point = #{soPoint,jdbcType=BIGINT})


        and fi.fa_point = #{faPoint,jdbcType=BIGINT}


        and fi.fa_clientele = #{faClientele,jdbcType=BIGINT}


        and fi.so_phone = #{soPhone,jdbcType=VARCHAR}


        and fi.so_clientele = #{soClientele,jdbcType=BIGINT}


        and fi.so_point = #{soPoint,jdbcType=BIGINT}


        and fi.freight = #{freight,jdbcType=INTEGER}


        and fi.collection = #{collection,jdbcType=INTEGER}


        and TO_DAYS(fi.ins_time) = TO_DAYS(#{insTime,jdbcType=TIMESTAMP})


        and TO_DAYS(fi.up_time) = TO_DAYS(#{upTime,jdbcType=TIMESTAMP})


        and fi.status = #{status,jdbcType=INTEGER}


        and fi.count_insurance = #{countInsurance,jdbcType=INTEGER}


        and fi.fee = #{fee,jdbcType=INTEGER}


        and fi.freight_way = #{freightWay,jdbcType=INTEGER}


        and fi.collection_way = #{collectionWay,jdbcType=INTEGER}


       and  fi.bank_code = #{bankCode,jdbcType=VARCHAR}


      and  fi.card_no = #{cardNo,jdbcType=VARCHAR}


       and  fi.so_way = #{soWay,jdbcType=INTEGER}


       and TO_DAYS(so_time) = TO_DAYS(#{soTime,jdbcType=TIMESTAMP})


       and TO_DAYS(collection_time) = TO_DAYS(#{collectionTime,jdbcType=TIMESTAMP})


       and fi.so_name like concat('%',#{soName,jdbcType=VARCHAR},'%')



      order by ${orderString}


      limit #{page.length} offset #{page.begin}

 select 
	count(*)
 from fa_invoice fi


     and fi.fa_no = #{faNo,jdbcType=VARCHAR}


    and  TO_DAYS(fi.fa_time) = TO_DAYS(#{faTime,jdbcType=TIMESTAMP})


     and fi.fa_phone = #{faPhone,jdbcType=VARCHAR}


   and (fi.fa_point = #{faPoint,jdbcType=BIGINT} and fi.so_point = #{soPoint,jdbcType=BIGINT})


     and fi.fa_point = #{faPoint,jdbcType=BIGINT}


     and fi.fa_clientele = #{faClientele,jdbcType=BIGINT}


     and fi.so_phone = #{soPhone,jdbcType=VARCHAR}


     and fi.so_clientele = #{soClientele,jdbcType=BIGINT}


     and fi.so_point = #{soPoint,jdbcType=BIGINT}


     and fi.freight = #{freight,jdbcType=INTEGER}


     and fi.collection = #{collection,jdbcType=INTEGER}


     and TO_DAYS(fi.ins_time) = TO_DAYS(#{insTime,jdbcType=TIMESTAMP})


     and TO_DAYS(fi.up_time) = TO_DAYS(#{upTime,jdbcType=TIMESTAMP})


     and fi.status = #{status,jdbcType=INTEGER}


     and fi.count_insurance = #{countInsurance,jdbcType=INTEGER}


     and fi.fee = #{fee,jdbcType=INTEGER}


     and fi.freight_way = #{freightWay,jdbcType=INTEGER}


     and fi.collection_way = #{collectionWay,jdbcType=INTEGER}


    and  fi.bank_code = #{bankCode,jdbcType=VARCHAR}


   and  fi.card_no = #{cardNo,jdbcType=VARCHAR}


    and  fi.so_way = #{soWay,jdbcType=INTEGER}


    and TO_DAYS(so_time) = TO_DAYS(#{soTime,jdbcType=TIMESTAMP})


    and TO_DAYS(collection_time) = TO_DAYS(#{collectionTime,jdbcType=TIMESTAMP})


   and  fi.so_name like concat('%',#{soName,jdbcType=VARCHAR},'%')

  	SELECT
      count(*) freight_way /*行数*/,
      sum(freight) freight,
      sum(collection) collection,
      sum(count_insurance) count_insurance,
      sum(fee) fee,
      sum(so_point_sub_money) so_point_sub_money,
			sum(cargo_num) cargo_num,
      sum(if(collection=0,0,1)) collection_way/*代收件数*/
		FROM
			fa_invoice fi


        and fi.fa_no = #{faNo,jdbcType=VARCHAR}


       and  TO_DAYS(fi.fa_time) = TO_DAYS(#{faTime,jdbcType=TIMESTAMP})


        and fi.fa_phone = #{faPhone,jdbcType=VARCHAR}


      and (fi.fa_point = #{faPoint,jdbcType=BIGINT} and fi.so_point = #{soPoint,jdbcType=BIGINT})


        and fi.fa_point = #{faPoint,jdbcType=BIGINT}


        and fi.fa_clientele = #{faClientele,jdbcType=BIGINT}


        and fi.so_phone = #{soPhone,jdbcType=VARCHAR}


        and fi.so_clientele = #{soClientele,jdbcType=BIGINT}


        and fi.so_point = #{soPoint,jdbcType=BIGINT}


        and fi.freight = #{freight,jdbcType=INTEGER}


        and fi.collection = #{collection,jdbcType=INTEGER}


        and TO_DAYS(fi.ins_time) = TO_DAYS(#{insTime,jdbcType=TIMESTAMP})


        and TO_DAYS(fi.up_time) = TO_DAYS(#{upTime,jdbcType=TIMESTAMP})


        and fi.status = #{status,jdbcType=INTEGER}


        and fi.count_insurance = #{countInsurance,jdbcType=INTEGER}


        and fi.fee = #{fee,jdbcType=INTEGER}


        and fi.freight_way = #{freightWay,jdbcType=INTEGER}


        and fi.collection_way = #{collectionWay,jdbcType=INTEGER}


       and  fi.bank_code = #{bankCode,jdbcType=VARCHAR}


      and  fi.card_no = #{cardNo,jdbcType=VARCHAR}


       and  fi.so_way = #{soWay,jdbcType=INTEGER}


       and TO_DAYS(so_time) = TO_DAYS(#{soTime,jdbcType=TIMESTAMP})


       and TO_DAYS(collection_time) = TO_DAYS(#{collectionTime,jdbcType=TIMESTAMP})


       and fi.so_name like concat('%',#{soName,jdbcType=VARCHAR},'%')



      limit #{page.length} offset #{page.begin}
